

module AbsHid where

-- Haskell module generated by the BNF converter




newtype MyIdent = MyIdent String deriving (Eq, Ord, Show, Read)
data Program = SCode [Stm]
  deriving (Eq, Ord, Show, Read)

data Function = Fun MyIdent [Decl] [Stm]
  deriving (Eq, Ord, Show, Read)

data Decl = Dec MyIdent
  deriving (Eq, Ord, Show, Read)

data Stm
    = SFun Function
    | SDecl Decl
    | SExp Exp
    | SBlock [Stm]
    | SWhile Exp Stm
    | SReturn Exp
    | SIf Exp Stm
    | SIfElse Exp Stm Stm
    | SFor Exp Exp Exp Stm
    | SPrt Exp
  deriving (Eq, Ord, Show, Read)

data Exp
    = EAss MyIdent Exp
    | EArAss MyIdent ArithAssignOp Exp
    | ELt Exp Exp
    | EGt Exp Exp
    | ELe Exp Exp
    | EGe Exp Exp
    | EEq Exp Exp
    | ENeq Exp Exp
    | EAdd Exp Exp
    | ESub Exp Exp
    | EMul Exp Exp
    | EDiv Exp Exp
    | EMod Exp Exp
    | EInc Exp
    | EDec Exp
    | EUmin Exp
    | ENeg Exp
    | EPreIn MyIdent
    | EPreDe MyIdent
    | EPstIn MyIdent
    | EPstDe MyIdent
    | Call MyIdent [Exp]
    | ELambda [Decl] [Stm]
    | EVar MyIdent
  deriving (Eq, Ord, Show, Read)

data ArithAssignOp
    = AssignAdd | AssignSubt | AssignMult | AssignDiv | AssignMod
  deriving (Eq, Ord, Show, Read)

